//	1부터 30까지의 정수에서 짝수만 한 줄에 3개씩 출력하라
public class 제어문_반복문_문제_7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 2; i <= 30; i += 2) {
			if (i <= 6) {
				System.out.print(i + " ");
			}
			
			}
		System.out.println();
		
		for (int i = 8; i <= 30; i += 2) {
			if (i <= 12) {
				System.out.print(i + " ");
			}
			
			}
		System.out.println();
		
		for (int i = 14; i <= 30; i += 2) {
			if (i <= 18) {
				System.out.print(i + " ");
			}
			
			}
		System.out.println();
		
		for (int i = 20; i <= 30; i += 2) {
			if (i <= 24) {
				System.out.print(i + " ");
			}
			
			}
		System.out.println();
		
		for (int i = 26; i <= 30; i += 2) {
			if (i <= 30) {
				System.out.print(i + " ");
			}
			
			}
		System.out.println();
		}

	}

